import React, { useState,useEffect } from 'react';
import './Quiz.scss';

const Quiz = () => {
    const [pre,setPre] = useState();
    const [ans,setAns] = useState();

    const onClickHandler = e => {
        setPre(ans);
        setAns(e.target.id);
    };

    //버튼 클릭시 색깔 변경
    useEffect(()=>{
        if(ans!=null){
            const element = document.getElementById(ans);
            element.style.backgroundColor = '#739072';
            element.style.color = 'white';
        }
        
        if(pre != null){
            const pelement = document.getElementById(pre);
            pelement.style.backgroundColor = '#ffffff';
            pelement.style.color = '#000';
        }
    },[ans]);

    return (
    <div className="quiz">
            <div className="MainTitle">텍스트 타입 퀘스트</div>
            <div className="SubTitle">이 부분에는 subtext가 삽입됩니다. </div>
        <div className="Content">
            <div className="ExplainBotton">
                <div className="Explain">Q1. 다음 중 올바르지 않게 연결된 단어를 고르시오</div>
            </div>
            <div className="QuizBody">
                <div className="AnswerBody">
                    <button onClick={onClickHandler} id='1' className="Answer">매수</button>
                    <button onClick={onClickHandler} id='2' className="Answer">주식</button>
                    <button onClick={onClickHandler} id='3' className="Answer">매도</button>
                    <button onClick={onClickHandler} id='4' className="Answer">공매도</button>
                </div>
                <div className="MeaningBody">
                    <div className="Meaning">주식을 사다</div>
                    <div className="Meaning">일상 생활에서 먹는 식사 </div>
                    <div className="Meaning">주식을 팔다</div>
                    <div className="Meaning">다른 사람의 것을 빌려서 <br />차익거래를 하는 방식</div>
                </div>
            </div>
        </div>
    </div>
    );
};
export default Quiz;
