import React, { useState,useEffect } from 'react';
import './Quest_text.scss';

const Quest_text = () =>
{
  const [ans,setAns] = useState('');
  const changeHandler = e => {
    setAns(e.target.value);
  }
  useEffect(()=>{
    console.log(ans);
  },[ans])
  return (
    <div className="Text">
      <div className="MainTitle">텍스트 타입 퀘스트</div>
      <div className="SubTitle" value={ans} onChange={changeHandler}>이부분에는 subtext가 삽입됩니다.<br /> </div>
      <div className="Content">
        <div className="Explain">이 부분에는 미션에 대한 설명이 들어갑니다
        </div>
        <textarea className="Box-text" placeholder='여기에 답을 입력해주세요.'></textarea>
        <button className="YesButton">
          <div className="Yes">확인</div>
        </button>
      </div>
    </div>
  );
};

export default Quest_text;
